<?php$captchacode = rand();?>               <img src="comment/Generate_Captcha_Code.php?rand=<?php echo $captchacode; ?>" id='captchaimg'  style="margin-top: 26px;">  <a href='javascript: refreshCaptcha();' >    <div class="refreshbox">        <div class="refresh-img" style="margin-top: -26px; margin-right: -10px;">                    <img style="border:none;" src="comment/img/refresh.png" title="Click to change the code"  class="pull-right" />        </div>    </div></a><script language='JavaScript' type='text/javascript'>    function refreshCaptcha() {        var img = document.images['captchaimg'];        var c = Math.round(Math.random() * 10000);        img.src = img.src.substring(0, img.src.lastIndexOf("?")) + "?rand=" + c;    }</script>